(1, 'bookingorderadmin', 'Booking Order - Admin', '<h4><strong>Order Information</strong></h4>\r\n\r\n<p>Invoice No.: {invoice_id}</p>\r\n\r\n<p>Invoice code {invoice_code}<br />\r\nDeposit Amount: {currency_code} {currency_sign} {deposit_amount}<br />\r\nTotal Amount: {currency_code} {currency_sign} {total_amount}</p>\r\n\r\n<h4><strong>Customer Information</strong></h4>\r\n\r\n<p>Customer ID: {customer_id}<br />\r\nName : {fullname}<br />\r\nEmail : {customer_email}<br />\r\nPhone : {phone}</p>\r\n\r\n<p>You can view invoice at {invoice_link}</p>\r\n', 'New Booking Notification', '{invoice_id} {invoice_code} {deposit_amount} {total_amount} {customer_email} {customer_id} {country} {phone} {currency_code} {currency_sign} {invoice_link} {site_title} {fullname}'),
(2, 'bookingpaidadmin', 'Booking Paid - Admin', '<h4><strong>Booking Paid Successfully</strong></h4>\n\n<p>Invoice No.: {invoice_id}</p>\n\n<p>Invoice code: {invoice_code}<br />\nPayment Method: {payment_method}<br />\nDeposit Amount: {currency_code} {currency_sign} {deposit_amount}<br />\nTotal Amount: {currency_code} {currency_sign} {total_amount}</p>\n\n<h4><strong>Customer Information</strong></h4>\n\n<p>Customer ID: {customer_id}<br />\nName : {fullname}<br />\nEmail : {customer_email}<br />\nCountry : {country}<br />\nPhone : {phone}</p>\n\n<p>You can view invoice at {invoice_link}</p>\n', 'Booking Payment Notification', '{invoice_id} {invoice_code} {deposit_amount} {total_amount} {customer_email} {customer_id} {country} {phone} {currency_code} {currency_sign} {invoice_link} {site_title} {fullname}'),
(3, 'supplierregister', 'Supplier Register', '<p>Thank you for registering as a Supplier. Your account is not verified yet so as soon as your account is verified you will receive your login details.</p>\r\n', 'Signed Up Successfully', ''),
(4, 'forgotpassword', 'Forgot Password', '<p>Below is the login information you requested<br />\r\nUsername : {username}<br />\r\nPassword: {password}<br />\r\n<br />\r\n<br />\r\nHave a question? Contact us. Do not reply to this email.</p>\r\n', 'Your Lost Login Information', '{username} {password}'),
(5, 'bookingordercustomer', 'Booking Order - Customer', '<p><strong>Dear </strong>{fullname} ,<br />\r\nThis is to notify you that you have made a booking with us details are as follows:<br />\r\n<br />\r\n<strong>Invoice #</strong> {invoice_id}&nbsp;<br />\r\n<strong>Invoice code</strong> {invoice_code}</p>\r\n\r\n<p>Deposit Amount: {currency_code} {currency_sign} {deposit_amount}<br />\r\nTotal Amount: {currency_code} {currency_sign} {total_amount}</p>\r\n\r\n<p><br />\r\nDue Date: {due_date}</p>\r\n\r\n<p>------------------------------------------------------<br />\r\nYou may review your invoice at any time by visiting at: {invoice_link}<br />\r\n{site_title} | Billing.</p>\r\n', 'Booking Details and Invoice', '{invoice_id} {invoice_code} {deposit_amount} {total_amount} {customer_email} {customer_id} {country} {phone} {currency_code} {currency_sign} {invoice_link} {site_title} {due_date} {fullname}'),
(6, 'bookingpaidcustomer', 'Booking Paid - Customer', '<p>Dear {fullname} ,<br />\r\nYour payment for booking is successfull. Details are as follows:<br />\r\nYour payment method is: {payment_method}<br />\r\nInvoice # {invoice_id}</p>\r\n\r\n<p>invoice code {invoice_code}<br />\r\nDeposit Amount: {currency_code} {currency_sign} {deposit_amount}<br />\r\nTotal Amount: {currency_code} {currency_sign} {total_amount}</p>\r\n\r\n<p>Remaining Amount: {currency_code} {currency_sign} {remaining_amount}</p>\r\n\r\n<p>------------------------------------------------------<br />\r\nYou may review your invoice at any time by visiting at: {invoice_link}<br />\r\n{site_title} | Billing.</p>\r\n', 'Booking Payment Successfull', '{invoice_id} {invoice_code} {deposit_amount} {total_amount} {customer_email} {customer_id} {country} {phone},{currency_code},{currency_sign} {invoice_link} {site_title} {remaining_amount} {fullname}'),
(7, 'supplierregisteradmin', 'Email Admin for Supplier Registration', '<p>New Agent has registered, following are details,</p>\r\n\r\n<p>Email: {email}</p>\r\n\r\n<p>Name: {fullname}</p>\r\n\r\n<p>Address: {address}</p>\r\n\r\n<p>Phone: {phone}</p>\r\n', 'New Agent Registered', '{email} {fullname} {address} {phone}'),
(8, 'verifyaccount', 'Verify New Account', '<p>Dear {fullname},</p>\r\n\r\n<p>Your Account has been verified your login details are as below :</p>\r\n\r\n<p>Email: {email}</p>\r\n\r\n<p>Password: {password}</p>\r\n\r\n<p>You can now login at {loginurl}</p>\r\n', 'Your Account has been verified', '{fullname} {email} {password} {loginurl}'),
